module.exports = (api, options, rootOptions) => {
  const utils = require('./utils')(api);

  // 加载以来
  api.extendPackage({
    dependencies: {
      'echarts': '^4.6.0'
    }
  });

  api.injectImports(utils.getMain(), `import './plugins/wyz-echarts/wyz-echarts.js'`);

  api.render({
    './src/plugins/wyz-echarts/wyz-echarts.js': './plugins/wyz-echarts.js',
  });

  if (options.theme === 'all') {
    api.render({
      './src/plugins/wyz-echarts/theme/sweet-light.json': './theme/sweet-light.json',
      './src/plugins/wyz-echarts/theme/sweet-light-16.json': './theme/sweet-light-16.json'
      // 暗色主题暂时未开发
    });
  } else if (options.theme === 'light') {
    api.render({
      './src/plugins/wyz-echarts/theme/sweet-light.json': './theme/sweet-light.json',
      './src/plugins/wyz-echarts/theme/sweet-light-16.json': './theme/sweet-light-16.json'
    });
  } else if (options.theme === 'dark') {
    // 暗色主题暂时未开发
  }
};
